 35      /**
 36       * Updated tags for a given music file.
 37       *
 38       * @return "UPDATED" if the new tags were updated, "SKIPPED" if no update was necessary.
 39       *         Otherwise the error message is returned.
 40       */
 41      @MessageMapping("/edit")
 42      @SendToUser(broadcast = false)
 43      public String setTags(@Validated TagData data) {
 44          try {
 45              MediaFile file = mediaFileService.getMediaFile(data.getMediaFileId());
 46              MusicFolder folder = mediaFolderService.getMusicFolderById(file.getFolderId());
 47  
 48              MetaDataParser parser = metaDataParserFactory.getParser(file.getFullPath(folder.getPath()));
 49  
 50              if (!parser.isEditingSupported()) {
 51                  return "Tag editing of " + FilenameUtils.getExtension(file.getPath()) + " files is not supported.";
 52              }
 53  
 54              if (StringUtils.equals(data.getArtist(), file.getArtist())
 55                      && StringUtils.equals(data.getAlbum(), file.getAlbumName())
 56                      && StringUtils.equals(data.getTitle(), file.getTitle())
 57                      && ObjectUtils.equals(data.getYear(), file.getYear())
 58                      && StringUtils.equals(data.getGenre(), file.getGenre())
 59                      && ObjectUtils.equals(data.getTrack(), file.getTrackNumber())) {
 60                  return "SKIPPED";
 61              }
 62  
 63              MetaData newMetaData = parser.getMetaData(file.getFullPath(folder.getPath()));
 64  
 65              // Note: album artist is intentionally not set, as it is not user-changeable.
 66              newMetaData.setArtist(data.getArtist());
 67              newMetaData.setAlbumName(data.getAlbum());
 68              newMetaData.setTitle(data.getTitle());
 69              newMetaData.setYear(data.getYear());
 70              newMetaData.setGenre(data.getGenre());
 71              newMetaData.setTrackNumber(data.getTrack());
 72              parser.setMetaData(file, newMetaData);
 73  
 74              mediaFileService.refreshMediaFile(file, folder);
 75              mediaFileService.refreshMediaFile(mediaFileService.getParentOf(file), folder);
 76              return "UPDATED";
 77  
 78          } catch (Exception x) {
 79              LOG.warn("Failed to update tags for {}", data.getMediaFileId(), x);
 80              return x.getMessage();
 81          }
 82      }
