168      /**
169       * Computes the CRC checksum for the given file.
170       *
171       * @param file The file to compute checksum for.
172       * @return A CRC32 checksum.
173       * @throws IOException If an I/O error occurs.
174       */
175      private static long computeCrc(Path file) throws IOException {
176          try (InputStream is = Files.newInputStream(file);
177                  BufferedInputStream bis = new BufferedInputStream(is);
178                  CheckedInputStream cis = new CheckedInputStream(bis, new CRC32())) {
179              byte[] buf = new byte[8192];
180              while ((cis.read(buf)) != -1) {
181                  continue;
182              }
183  
184              return cis.getChecksum().getValue();
185          }
186      }
