192      private ResponseDTO prepareResponse(List<MediaFile> files, List<Integer> indices,
193              Supplier<TransferStatus> statusSupplier, Consumer<TransferStatus> statusCloser, Collection<Pair<Path, Integer>> additionalFiles)
194              throws IOException {
195          if (indices == null) {
196              indices = IntStream.range(0, files.size()).boxed().collect(Collectors.toList());
197          } else if (indices.parallelStream().anyMatch(i -> i >= files.size())) {
198              throw new IllegalArgumentException("Can't have index > number of files");
199          }
200  
201          if (indices.size() == 0) {
202              // nothing, just return empty
203              return new ResponseDTO(null, "emptyfile.download", 0, -1);
204          }
205  
206          if (indices.size() == 1 && (additionalFiles == null || additionalFiles.size() == 0)) {
207              // single file
208              MediaFile file = files.get(indices.get(0));
209              Path path = file.getFullPath(mediaFolderService.getMusicFolderById(file.getFolderId()).getPath());
210              long changed = file.getChanged() == null ? -1 : file.getChanged().toEpochMilli();
211              return new ResponseDTO(
212                      new MonitoredResource(
213                              new FileSystemResource(path),
214                              settingsService.getDownloadBitrateLimiter(),
215                              statusSupplier,
216                              statusCloser,
217                          (input, status) -> {}),
218                      path.getFileName().toString(),
219                      file.getFileSize(),
220                      changed);
221          } else {
222              // get a list of all paths under the tree, plus their zip names and sizes
223              Collection<Pair<Path, Pair<String, Long>>> pathsToZip = Stream
224                      .concat(
225                              indices.stream().map(files::get).filter(Objects::nonNull).map(x -> Pair.of(x.getRelativePath(), x.getFolderId())),
226                              additionalFiles.stream().filter(Objects::nonNull))
227                      .flatMap(pf -> {
228                          MusicFolder mf = mediaFolderService.getMusicFolderById(pf.getRight());
229                          Path p = mf.getPath().resolve(pf.getLeft());
230                          Path parent = p.getParent();
231                          try (Stream<Path> paths = Files.walk(p)) {
232                              return paths
233                                      .filter(f -> !f.getFileName().toString().startsWith("."))
234                                      .map(f -> {
235                                          String zipName = parent.relativize(f).toString();
236                                          long size = 0L;
237                                          if (Files.isRegularFile(f)) {
238                                              size = FileUtil.size(f);
239                                          } else {
240                                              zipName = zipName + '/';
241                                          }
242                                          return Pair.of(f, Pair.of(zipName, size));
243                                      })
244                                      // need to create a new stream, because try-with-resources will close the paths stream before it exits
245                                      .collect(Collectors.toList()).stream();
246                          } catch (Exception e) {
247                              LOG.warn("Error retrieving file to zip", e);
248                              return Stream.empty();
249                          }
250                      }).filter(Objects::nonNull)
251                      // need to preserve order because zip file creation needs to create folders first then files
252                      .collect(Collectors.toCollection(LinkedHashSet::new));
253  
254              // zip to out
255              BiConsumer<InputStream, TransferStatus> poutInit = (input, status) -> {
256                  PipedInputStream pin = (PipedInputStream) input;
257  
258                  // start a new thread to feed data in
259                  new Thread(() -> {
260                      try (PipedOutputStream pout = new PipedOutputStream(pin);
261                              ZipOutputStream zout = new ZipOutputStream(pout)) {
262                          zout.setMethod(ZipOutputStream.STORED); // No compression.
263                          pathsToZip.stream().forEach(LambdaUtils.uncheckConsumer(f -> {
264                              status.setExternalFile(f.getLeft());
265                              ZipEntry zipEntry = new ZipEntry(f.getRight().getKey());
266                              zipEntry.setSize(f.getRight().getValue());
267                              zipEntry.setCompressedSize(f.getRight().getValue());
268  
269                              if (f.getRight().getKey().endsWith("/") && f.getRight().getValue() == 0L) {
270                                  // directory
271                                  zipEntry.setCrc(0);
272                                  zout.putNextEntry(zipEntry);
273                              } else {
274                                  zipEntry.setCrc(computeCrc(f.getLeft()));
275                                  zout.putNextEntry(zipEntry);
276                                  Files.copy(f.getLeft(), zout);
277                              }
278  
279                              zout.closeEntry();
280                          }));
281                      } catch (Exception e1) {
282                          LOG.debug("Error with output to zip", e1);
283                      }
284                  }, "DownloadControllerDatafeed").start();
285  
286                  // wait for src data thread to connect
287                  while (pin.source == null) {
288                      // sit and wait and ponder life
289                  }
290              };
291  
292              long size = zipSize(pathsToZip.stream().map(e -> e.getRight()));
293  
294              PipedInputStream pin = new PipedInputStream(null, 16 * 1024); // 16 Kb buffer
295  
296              return new ResponseDTO(
297                      new MonitoredResource(
298                              new KnownLengthInputStreamResource(pin, size),
299                              settingsService.getDownloadBitrateLimiter(),
300                              statusSupplier,
301                              statusCloser,
302                              poutInit),
303                      null, size, -1);
304          }
305      }
