343      /**
344       *
345       * Class that ensures a stream meets at least minimum length. If underlying
346       * stream is shorter, then this will pad stream with zeros until threshold is
347       * met If underlying stream is longer, then this will warn as soon as it goes
348       * over.
349       *
350       */
351      private static class ThresholdInputStream extends FilterInputStream {
352          private long threshold;
353          private long bytesRead = 0;
354  
355          protected ThresholdInputStream(InputStream in, long threshold) {
356              super(in);
357              this.threshold = threshold;
358          }
359  
360          private void warn() {
361              LOG.warn("Stream output exceeded expected length of {}. It is likely that "
362                      + "the transcoder is not adhering to the bitrate limit or the media "
363                      + "source is corrupted or has grown larger", threshold);
364          }
365  
366          @Override
367          public int read() throws IOException {
368              int read = super.read();
369              if (read == -1 && bytesRead < threshold) {
370                  read = 0;
371              } else if (read != -1 && bytesRead == threshold) {
372                  warn();
373              }
374  
375              bytesRead++;
376  
377              return read;
378          }
379  
380          private static byte[] zeros = new byte[4096];
381  
382          @Override
383          public int read(byte b[], int off, int len) throws IOException {
384              int read = super.read(b, off, len);
385              if (read == -1 && bytesRead < threshold) {
386                  if (zeros.length < len) {
387                      zeros = new byte[len];
388                  }
389                  System.arraycopy(zeros, 0, b, off, len);
390                  read = Math.min(len, (int) (threshold - bytesRead));
391              } else if (read != -1 && bytesRead <= threshold && bytesRead + read > threshold) {
392                  warn();
393              }
394  
395              bytesRead += read;
396  
397              return read;
398          }
399  
400      }
