128      /**
129       * Sets the gain.
130       *
131       * @param gain The gain between 0.0 and 1.0.
132       */
133      public void setGain(float gain) {
134          if (gainControl != null) {
135  
136              double minGainDB = gainControl.getMinimum();
137              double maxGainDB = Math.min(0.0, gainControl.getMaximum());  // Don't use positive gain to avoid distortion.
138              double ampGainDB = 0.5f * maxGainDB - minGainDB;
139              double cste = Math.log(10.0) / 20;
140              double valueDB = minGainDB + (1 / cste) * Math.log(1 + (Math.exp(cste * ampGainDB) - 1) * gain);
141  
142              valueDB = Math.min(valueDB, maxGainDB);
143              valueDB = Math.max(valueDB, minGainDB);
144  
145              gainControl.setValue((float) valueDB);
146          }
147      }
