150       * Returns the position in seconds.
151       */
152      public int getPosition() {
153          return (int) (line.getMicrosecondPosition() / 1000000L);
154      }
155  
156      private void setState(State state) {
157          if (this.state.getAndSet(state) != state && listener != null) {
158              listener.stateChanged(this, state);
159          }
160      }
161  
162      private class AudioDataWriter implements Runnable {
163  
164          public AudioDataWriter() {
165              new Thread(this).start();
166          }
167  
168          @Override
169          public void run() {
170              try {
171                  byte[] buffer = new byte[line.getBufferSize()];
172  
173                  while (true) {
174  
175                      switch (state.get()) {
176                          case CLOSED:
177                          case EOM:
178                              return;
179                          case PAUSED:
180                              Thread.sleep(250);
181                              break;
182                          case PLAYING:
183                              // Fill buffer in order to ensure that write() receives an integral number of frames.
184                              int n = fill(buffer);
185                              if (n == -1) {
186                                  setState(EOM);
187                                  return;
188                              }
189                              line.write(buffer, 0, n);
190                              break;
191                      }
192                  }
193              } catch (Throwable x) {
194                  LOG.warn("Error when copying audio data: " + x, x);
195              } finally {
196                  close();
197              }
198          }
199  
200          private int fill(byte[] buffer) throws IOException {
201              int bytesRead = 0;
202              while (bytesRead < buffer.length) {
203                  int n = in.read(buffer, bytesRead, buffer.length - bytesRead);
204                  if (n == -1) {
205                      return bytesRead == 0 ? -1 : bytesRead;
206                  }
207                  bytesRead += n;
208              }
209              return bytesRead;
210          }
211      }
212  
213      public interface Listener {
214          void stateChanged(AudioPlayer player, State state);
215      }
216  
217      public static enum State {
218          PAUSED,
219          PLAYING,
220          CLOSED,
221          EOM
222      }
223  }

