 32  @Service
 33  public class PathWatcherService {
 34      private static final Logger LOG = LoggerFactory.getLogger(PathWatcherService.class);
 35  
 36      private final ExecutorService watcherThread = Executors.newSingleThreadExecutor(Util.getDaemonThreadfactory("path-watcher-thread-"));
 37      private final ExecutorService watcherFunctionThreadPool = Executors.newCachedThreadPool(Util.getDaemonThreadfactory("path-watcher-function-thread-"));
 38  
 39      private final Map<WatchKey, Map<Kind<? extends Object>, BiConsumer<Path, WatchEvent<Path>>>> watchFunctions = new ConcurrentHashMap<>();
 40      private final Map<String, WatchKey> watchNames = new ConcurrentHashMap<>();
 41      private WatchService watchService;
 42      private final Runnable watcherTask = () -> {
 43          for (;;) {
 44              WatchKey key;
 45              try {
 46                  key = watchService.take();
 47              } catch (Exception e) {
 48                  LOG.warn("Watch service unable to poll. Directory monitoring may be off", e);
 49                  continue;
 50              }
 51              Map<Kind<? extends Object>, BiConsumer<Path, WatchEvent<Path>>> fs = watchFunctions.get(key);
 52              if (fs == null) {
 53                  continue;
 54              }
 55              for (WatchEvent<?> event : key.pollEvents()) {
 56                  Optional.ofNullable(fs.get(event.kind())).ifPresent(f -> watcherFunctionThreadPool.submit(() -> f.accept((Path) key.watchable(), (WatchEvent<Path>) event)));
 57              }
 58              // To keep receiving events
 59              key.reset();
 60          }
 61      };
 62  
 63      public PathWatcherService() throws Exception {
 64          this.watchService = FileSystems.getDefault().newWatchService();
 65          watcherThread.submit(watcherTask);
 66      }
 67  
 68      public void setWatcher(Path watchable, BiConsumer<Path, WatchEvent<Path>> entryCreateConsumer,
 69              BiConsumer<Path, WatchEvent<Path>> entryDeleteConsumer,
 70              BiConsumer<Path, WatchEvent<Path>> entryModifyConsumer, BiConsumer<Path, WatchEvent<Path>> overflowConsumer)
 71              throws IOException {
 72          setWatcher(UUID.randomUUID().toString(), watchable, entryCreateConsumer, entryDeleteConsumer, entryModifyConsumer, overflowConsumer);
 73      }
 74  
 75      public void setWatcher(String id, Path watchable, BiConsumer<Path, WatchEvent<Path>> entryCreateConsumer,
 76              BiConsumer<Path, WatchEvent<Path>> entryDeleteConsumer,
 77              BiConsumer<Path, WatchEvent<Path>> entryModifyConsumer, BiConsumer<Path, WatchEvent<Path>> overflowConsumer)
 78              throws IOException {
 79          Map<Kind<? extends Object>, BiConsumer<Path, WatchEvent<Path>>> fnMap = new HashMap<>();
 80          fnMap.put(StandardWatchEventKinds.ENTRY_CREATE, entryCreateConsumer);
 81          fnMap.put(StandardWatchEventKinds.ENTRY_DELETE, entryDeleteConsumer);
 82          fnMap.put(StandardWatchEventKinds.ENTRY_MODIFY, entryModifyConsumer);
 83          fnMap.put(StandardWatchEventKinds.OVERFLOW, overflowConsumer);
 84          setWatcher(id, watchable, fnMap);
 85      }
 86  
 87      public void setWatcher(String id, Path watchable, Map<Kind<? extends Object>, BiConsumer<Path, WatchEvent<Path>>> fnMap) throws IOException {
 88          WatchKey key = watchable.register(watchService, StandardWatchEventKinds.ENTRY_CREATE, StandardWatchEventKinds.ENTRY_DELETE, StandardWatchEventKinds.ENTRY_MODIFY, StandardWatchEventKinds.OVERFLOW);
 89          watchFunctions.put(key, fnMap);
 90          watchNames.compute(id, (k, v) -> {
 91              if (!key.equals(v)) {
 92                  invalidateWatcher(v);
 93              }
 94              return key;
 95          });
 96      }
 97  
 98      public void invalidateWatcher(String id) {
 99          WatchKey key = watchNames.remove(id);
100          invalidateWatcher(key);
101      }
102  
103      public void invalidateWatcher(Path watchable) {
104          watchNames.entrySet().stream()
105              .filter(e -> e.getValue().watchable().equals(watchable))
106              .findAny()
107              .ifPresent(e -> invalidateWatcher(e.getKey()));
108      }
109  
110      public void invalidateWatcher(WatchKey key) {
111          if (key != null) {
112              key.cancel();
113              watchFunctions.remove(key);
114          }
115      }
