 32  @Service
 33  public class PathWatcherService {
 34      private static final Logger LOG = LoggerFactory.getLogger(PathWatcherService.class);
 35  
 36      private final ExecutorService watcherThread = Executors.newSingleThreadExecutor(Util.getDaemonThreadfactory("path-watcher-thread-"));
 37      private final ExecutorService watcherFunctionThreadPool = Executors.newCachedThreadPool(Util.getDaemonThreadfactory("path-watcher-function-thread-"));
 38  
 39      private final Map<WatchKey, Map<Kind<? extends Object>, BiConsumer<Path, WatchEvent<Path>>>> watchFunctions = new ConcurrentHashMap<>();
 40      private final Map<String, WatchKey> watchNames = new ConcurrentHashMap<>();
 41      private WatchService watchService;
 42      private final Runnable watcherTask = () -> {
 43          for (;;) {
 44              WatchKey key;
 45              try {
 46                  key = watchService.take();
 47              } catch (Exception e) {
 48                  LOG.warn("Watch service unable to poll. Directory monitoring may be off", e);
 49                  continue;
 50              }
 51              Map<Kind<? extends Object>, BiConsumer<Path, WatchEvent<Path>>> fs = watchFunctions.get(key);
 52              if (fs == null) {
 53                  continue;
 54              }
 55              for (WatchEvent<?> event : key.pollEvents()) {
 56                  Optional.ofNullable(fs.get(event.kind())).ifPresent(f -> watcherFunctionThreadPool.submit(() -> f.accept((Path) key.watchable(), (WatchEvent<Path>) event)));
 57              }
 58              // To keep receiving events
 59              key.reset();
 60          }
 61      };
 62  
 63      public PathWatcherService() throws Exception {
 64          this.watchService = FileSystems.getDefault().newWatchService();
 65          watcherThread.submit(watcherTask);
 66      }
