 30  public class HlsSession {
 31      private final Logger LOG;
 32  
 33      private static final ScheduledExecutorService EXECUTOR = Executors.newSingleThreadScheduledExecutor(Util.getDaemonThreadfactory("hls-destroy-session"));
 34  
 35      private static final long SESSION_TIMEOUT_SECONDS = 120L;
 36  
 37      private final Key sessionKey;
 38  
 39      private final MediaFile mediaFile;
 40  
 41      private final TranscodingService transcodingService;
 42  
 43      private Process process;
 44  
 45      private ScheduledFuture<?> destroySessionFuture;
 46  
 47      public HlsSession(Key sessionKey, MediaFile mediaFile, TranscodingService transcodingService) {
 48          this.LOG = LoggerFactory.getLogger(HlsSession.class.toString() + "-" + sessionKey.id());
 49          this.LOG.info("Creating HLS session {}: {}", sessionKey.id(), sessionKey);
 50          this.sessionKey = sessionKey;
 51          this.mediaFile = mediaFile;
 52          this.transcodingService = transcodingService;
 53      }
 54  
 55      public Path waitForSegment(int segmentIndex, long timeoutMillis) throws Exception {
 56          this.LOG.debug("Requesting hls segment {}", segmentIndex);
 57          scheduleSessionDestruction();
 58          Path segment = getSegment(segmentIndex);
 59          if (segment != null) {
 60              this.LOG.debug("Segment {} already produced.", segmentIndex);
 61              return segment;
 62          }
 63          if (!isProcessAlive()) {
 64              startProcess(segmentIndex);
 65          } else {
 66              Integer latestCompleted = getLatestCompletedSegmentIndex();
 67              if (latestCompleted != null
 68                      && (segmentIndex < latestCompleted.intValue() || segmentIndex > latestCompleted.intValue() + 2)) {
 69                  destroySession();
 70                  startProcess(segmentIndex);
 71              }
 72          }
 73          long timeout = currentTimeMillis() + timeoutMillis;
 74          while (segment == null && currentTimeMillis() < timeout && isProcessAlive()) {
 75              this.LOG.debug("Segment {} not yet produced. Waiting.", segmentIndex);
 76              Thread.sleep(2000L);
 77              segment = getSegment(segmentIndex);
 78          }
 79          if (segment != null) {
 80              this.LOG.debug("Segment {} produced.", segmentIndex);
 81          } else {
 82              this.LOG.warn("Timed out for segment {}", segmentIndex);
 83          }
 84          return segment;
 85      }
 86  
 87      private synchronized void scheduleSessionDestruction() {
 88          if (this.destroySessionFuture != null)
 89              this.destroySessionFuture.cancel(false);
 90          this.destroySessionFuture = EXECUTOR.schedule(() -> this.destroySession(), SESSION_TIMEOUT_SECONDS, TimeUnit.SECONDS);
 91      }
 92  
 93      public void destroySession() {
 94          this.LOG.debug("Destroying hls session");
 95          killProcess();
 96          FileUtil.delete(getDirectory());
 97      }
 98  
 99      private Path getSegment(int segmentIndex) {
100          SortedSet<Integer> segmentIndexes = getCompletedSegmentIndexes();
101          return segmentIndexes.contains(Integer.valueOf(segmentIndex)) ? getDirectory().resolve(segmentIndex + ".ts") : null;
102      }
103  
104      private Integer getLatestCompletedSegmentIndex() {
105          SortedSet<Integer> completed = getCompletedSegmentIndexes();
106          return completed.isEmpty() ? null : completed.last();
107      }
108  
109      private SortedSet<Integer> getCompletedSegmentIndexes() {
110          try (Stream<Path> children = Files.list(getDirectory())) {
111              SortedSet<Integer> result = children.filter(Files::isRegularFile)
112                      .filter(c -> "ts".equals(MoreFiles.getFileExtension(c))).map(MoreFiles::getNameWithoutExtension)
113                      .map(Integer::valueOf).collect(Collectors.toCollection(() -> new TreeSet<>()));
114              if (!result.isEmpty() && isProcessAlive()) {
115                  result.remove(result.last());
116              }
117              return result;
118          } catch (IOException e) {
119              LOG.warn("Could not retrieve directory list for {} to find segment files", getDirectory(), e);
120  
121              return Collections.emptySortedSet();
122          }
123      }

