 80      public static <T> List<T> subList(List<T> list, long offset, long max) {
 81          if (list.size() == Integer.MAX_VALUE) {
 82              return list.stream().skip(offset).limit(max).collect(toList());
 83          }
 84          return list.subList(Math.min(list.size(), Ints.saturatedCast(offset)), Math.min(list.size(), Ints.saturatedCast(offset + max)));
